package com.rowdyruff.smarthack.controllers;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.security.authentication.AuthenticationManager;
import org.springframework.security.authentication.BadCredentialsException;
import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestHeader;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RestController;

import com.rowdyruff.domain.Institution;
import com.rowdyruff.domain.Role;
import com.rowdyruff.domain.User;
import com.rowdyruff.repository.UserRepository;
import com.rowdyruff.smarthack.model.AuthenticationRequest;
import com.rowdyruff.smarthack.model.AuthenticationResponse;
import com.rowdyruff.smarthack.model.CreateUserRequest;
import com.rowdyruff.smarthack.model.RegisterForm;
import com.rowdyruff.smarthack.service.InstitutionService;
import com.rowdyruff.smarthack.service.UserService;
import com.rowdyruff.smarthack.service.MyUserDetailsService;
import com.rowdyruff.smarthack.utils.JwtUtils;

@RestController
@RequestMapping("/users")
public class UserController extends GenericController<User> {

	private static final long serialVersionUID = 5663450026672409570L;

	@Autowired
	private AuthenticationManager authenticationManager;
	
	@Autowired
	private MyUserDetailsService userDetailsService;
	
	@Autowired
	private UserService userService;
	
	@Autowired
	private UserRepository userRepository;
	
	@Autowired
	private InstitutionService institutionService;
	
	private String b64 = "";
	
	@Autowired
	private JwtUtils jwtTokenUtil;

	public UserController(UserService userService) {
		super(userService);
	}
	
	@RequestMapping(value = "/hello", method = RequestMethod.GET)
	public String firstPage() {
		return "Hello world";
	}
	
	@RequestMapping(value = "/account", method = RequestMethod.GET) 
	public ResponseEntity<?> getAccountDetails(@RequestHeader("Authorization") String jwt) {
		try {
			String username = jwtTokenUtil.extractUsername(jwt);
			User user = userRepository.findByUsername(username);
			
			return ResponseEntity.ok(user);
		} catch (Exception ex) {
			ex.printStackTrace();
			return ResponseEntity.status(HttpStatus.BAD_REQUEST).body(null);
		}
	}
	
	@RequestMapping(value="/getB64String", method = RequestMethod.GET) 
	public ResponseEntity<?> getPdf() {
		return ResponseEntity.ok(b64);
	}
	
	@RequestMapping(value = "/authenticate", method = RequestMethod.POST)
	public ResponseEntity<?> createAuthenticationToken(@RequestBody AuthenticationRequest authenticationRequest) throws Exception {
		try {
			authenticationManager.authenticate(
					new UsernamePasswordAuthenticationToken(authenticationRequest.getUsername(), authenticationRequest.getPassword()));
		} catch(BadCredentialsException e) {
			throw new Exception("Incorrect username or password", e);
		}
		
		final UserDetails userDetails = userDetailsService
				.loadUserByUsername(authenticationRequest.getUsername());
		
		final String jwt = jwtTokenUtil.generateToken(userDetails);
		User user = userRepository.findByUsername(authenticationRequest.getUsername());
		
		return ResponseEntity.ok(new AuthenticationResponse(user, jwt));
	}
	
	@RequestMapping(value = "/register", method = RequestMethod.POST)
	public ResponseEntity<?> register(@RequestBody RegisterForm registerForm) {
		User user = new User(registerForm.getUsername(), registerForm.getPassword());
		user.setRole(Role.ROLE_CLIENT);
		return ResponseEntity.ok(userRepository.create(new User(registerForm.getUsername(), registerForm.getPassword())));
	}
	
	@PostMapping
	public ResponseEntity<?> createUser(@RequestBody CreateUserRequest req) {
		try {
			Institution institution = null;
			if (req.getRole() != null && (req.getRole().equals(Role.ROLE_INSTITUTION_ADMIN.name()) || req.getRole().equals(Role.ROLE_INSTITUTION_ADMIN.name())))
				institution = institutionService.getItem(req.getInstitutionId());
			
			User user = new User(req, institution);
			user = userRepository.create(user);
			return ResponseEntity.ok(user);
		} catch (Exception ex) {
			ex.printStackTrace();
			return ResponseEntity.status(HttpStatus.BAD_REQUEST).body(null);
		} 
	}

	@Override
	protected User getEmptyItem() {
		return new User();
	}

	@Override
	protected Boolean isNew(User item) {
		return (item.getId() == null) || item.getId() < 1;
	}
	
}
